import { Collapse, Table, Card } from 'antd';
import React, { useEffect, useState } from 'react';
import { getParamBySpotId } from '../../Api';
import NewParamModal from '../NewParam/NewParamModal';
const axios = require('axios');

const { Panel } = Collapse;

interface spot {
  coords: {
      coordX: number,
      coordY: number
  },
  _id: string,
  name: string,
  params: param[]
}

interface param {
  paramName: string,
  value: number
}

const columns =  [
  {
    title: 'Nome',
    dataIndex: 'paramName',
    key: 'name',
  },
  {
    title: 'Valor',
    dataIndex: 'value',
    key: 'value',
  },
  {
    title: 'Data da coleta',
    dataIndex: 'date',
    key: 'date',
  }
];

const SpotCard: React.FC<{spots: spot[]}> = ({spots}) => {
  const [date, setDate] = useState();

  if (spots === [{
    coords: {
        coordX: 2,
        coordY: 2
    },
    _id: '',
    name: '',
    params: []
  }]) return (<></>)
  return (
    <>
    {spots.map((spot) => 
        <>
          <Collapse style={{backgroundColor: 'white', borderColor: 'white'}}>
            <Panel header={spot.name} key={spot.name} style={{margin: 10, backgroundColor: '#e8e8e8'}}>
            {getParamBySpotId(spot._id).map((param: any) => console.log(param.paramName))}
              <div style={{display: 'flex', flexDirection: 'row'}}>
                <Card title='Coordenada X' style={{width: '50%'}}>
                  <p>{spot.coords.coordX}</p>
                </Card>
                <Card title='Coordenada Y'  style={{width: '50%'}}>
                  <p>{spot.coords.coordY}</p>
                </Card>
              </div>
              <br></br>
              <Table columns={columns} dataSource={spot.params} />
              <NewParamModal/>
            </Panel>
          </Collapse>
        </>)}
    </>
  );
};

export default SpotCard;
