import { Button, Modal, Form, Input, DatePicker, Select } from 'antd';
import React, { useState } from 'react';

const Unity  = {
  aluminioDissolvido: 'mg/l',
  arsenioTotal: 'mg/l',
  // "chumboTotal"
  // "cobreDissolvido"
  escherichiaColi: 'NMP/100ml'
  // "cromoTotal"
  // "cadmioTotal"
  // "dbo"
}

const NewParamModal: React.FC = () => {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [date, setDate] = useState();
  const [paramUnity, setParamUnity] = useState('');



  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const [nameSelected, setNameSelected] = useState(Unity);  
  const onFinish = (values: any) => {
    console.log('Success:', values);
  };

  const paramNames = [
    {
    "name": "Aluminio dissolvido",
    "value": "aluminioDissolvido",
    "unity": "mg/l"
   },
   {
    "name":"Arsênio total",
    "value":"arsenioTotal",
    "unity": "mg/l"
   },
   {
    "name":"Chumbo total",
    "value":"chumboTotal",
    "unity": "mg/l"
   },
  //  {
  //   "name":"Cobre dissolvido",
  //   "value":"cobreDissolvido"
  //  },
  //  {
  //   "name":"Escherichia coli",
  //   "value":"escherichiaColi"
  //  },
  //  {
  //   "name":"Cromo total",
  //   "value":"cromoTotal"
  //  },
  //  {
  //   "name":"Cádmio total",
  //   "value":"cadmioTotal"
  //  },
  //  {
  //   "name":"DBO",
  //   "value":"dbo"
  //  }
]

  const onFinishFailed = (errorInfo: any) => {
    console.log('Failed:', errorInfo);
  };

  console.log(date)

  return (
    <>
      <Button type="primary" onClick={showModal}>
        Cadastrar novo parametro
      </Button>
      <Modal title="Novo Paramentro" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
        <Form
        name="basic"
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 16 }}
        initialValues={{ remember: true }}
        onFinish={onFinish}
        onFinishFailed={onFinishFailed}
        autoComplete="off"
      >
        <Form.Item
          label="Nome"
          name="name"
          rules={[{ required: true, message: 'Selecione o nome do parâmetro' }]}
        >
          <Select onChange={(event) => setNameSelected(event.target.value)}>
            {paramNames.map((param) => <Select.Option  key={param.value} value={param.value} onSelect={setParamUnity(param.unity)}>{param.name}</Select.Option>)}
          </Select>
        </Form.Item>

        <Form.Item
          label="Valor"
          name="value"
          rules={[{ required: true, message: 'Insira um valor válido' }]}
        >
          <Input disabled={!nameSelected} placeholder={select}/>
        </Form.Item>
        <Form.Item 
        label="Data de coleta"
        rules={[{ required: true, message: 'Selecione uma data válida' }]}
        >
            <DatePicker value={date}/>
          </Form.Item>
      </Form>
      </Modal>
    </>
  );
};

export default NewParamModal;
