import { Collapse, Table, Card } from 'antd';
import React, { useEffect, useState } from 'react';
import NewParamModal from '../NewParam/NewParamModal';
const axios = require('axios');

const { Panel } = Collapse;

const columns =  [
  {
    title: 'Nome',
    dataIndex: 'name',
    key: 'name',
  },
  {
    title: 'Valor',
    dataIndex: 'value',
    key: 'value',
  },
  {
    title: 'Data da coleta',
    dataIndex: 'date',
    key: 'date',
  },
  {
    title: 'Limite',
    dataIndex: 'limite',
    key: 'limite',
  },
];

const data = [
  {
    key: '1',
    name: 'Cobre dissolvido',
    value: 32,
    date: '23/01/2022',
    limite: 'Viola'
  },
  {
    key: '2',
    name: 'Chumbo total',
    value: 25,
    date: '23/01/2022',
    limite: 'Cumpre'
  },
]

const SpotCard: React.FC = () => {
  const [spots, setSpots] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:1800/point").then((res: any) => {
      setSpots(res.data)
    })
  }, [])

  return (
    <>
      {spots.map((spot) => {
         <Collapse  style={{backgroundColor: 'white'}}>
         <Panel header={"Nome do ponto"} key="1" style={{margin: 20, backgroundColor: '#e8e8e8'}}>
           <div style={{display: 'flex', flexDirection: 'row'}}>
           <Card title='Coordenada X' style={{width: '50%'}}>
             <p>234,65</p>
          </Card>
          <Card title='Coordenada Y'  style={{width: '50%'}}>
             <p>234,65</p>
          </Card>
           </div>
           <br></br>
           <Table columns={columns} dataSource={data} />
           <NewParamModal/>
         </Panel>
       </Collapse>
      })}
    </>
  
    // <Collapse  style={{backgroundColor: 'white'}}>
    //   <Panel header="Nome do ponto" key="1" style={{margin: 20, backgroundColor: '#e8e8e8'}}>
    //     <div style={{display: 'flex', flexDirection: 'row'}}>
    //     <Card title='Coordenada X' style={{width: '50%'}}>
    //       <p>234,65</p>
    //    </Card>
    //    <Card title='Coordenada Y'  style={{width: '50%'}}>
    //       <p>234,65</p>
    //    </Card>
    //     </div>
    //     <br></br>
    //     <Table columns={columns} dataSource={data} />
    //     <NewParamModal/>
    //   </Panel>
    // </Collapse>
  );
};

export default SpotCard;
