import { Button, Modal } from 'antd';
import React, { useState } from 'react';
import NewParamForm from './NewParamForm';

const NewParamModal: React.FC = () => {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [ paramCreated, setParamCreated ] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setParamCreated(true);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <>
      <Button type="primary" onClick={showModal}>
        Cadastrar novo parametro
      </Button>
      <Modal title="Novo Paramentro" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
        <NewParamForm created={paramCreated}/>
      </Modal>
    </>
  );
};

export default NewParamModal;
