import { Collapse, Table, Card } from 'antd';
import React, { useEffect, useState } from 'react';
import NewParamModal from '../NewParam/NewParamModal';
const axios = require('axios');

const { Panel } = Collapse;
interface getSpots {
  data: spot[]
}

interface spot {
  coords: {
      coordX: number,
      coordY: number
  },
  _id: string,
  name: string,
}

const columns =  [
  {
    title: 'Nome',
    dataIndex: 'name',
    key: 'name',
  },
  {
    title: 'Valor',
    dataIndex: 'value',
    key: 'value',
  },
  {
    title: 'Data da coleta',
    dataIndex: 'date',
    key: 'date',
  },
  {
    title: 'Limite',
    dataIndex: 'limite',
    key: 'limite',
  },
];

const data = [
  {
    key: '1',
    name: 'Cobre dissolvido',
    value: 32,
    date: '23/01/2022',
    limite: 'Viola'
  },
  {
    key: '2',
    name: 'Chumbo total',
    value: 25,
    date: '23/01/2022',
    limite: 'Cumpre'
  },
]

const SpotCard: React.FC = () => {
  const [spots, setSpots] = useState([{
    coords: {
        coordX: 2,
        coordY: 2
    },
    _id: '',
    name: '',
  }]);

  useEffect(() => {
    fetch.then((res: getSpots) => setSpots(res.data))
  }, [])

  if (spots === [{
    coords: {
        coordX: 2,
        coordY: 2
    },
    _id: '',
    name: '',
  }]) return (<></>)
  return (
    <>
    {spots.map((spot) => 
        <>
          <Collapse  style={{backgroundColor: 'white', borderColor: 'white'}}>
            <Panel header={spot.name} key={spot.name} style={{margin: 10, backgroundColor: '#e8e8e8'}}>
              <div style={{display: 'flex', flexDirection: 'row'}}>
                <Card title='Coordenada X' style={{width: '50%'}}>
                  <p>{spot.coords.coordX}</p>
                </Card>
                <Card title='Coordenada Y'  style={{width: '50%'}}>
                  <p>{spot.coords.coordY}</p>
                </Card>
              </div>
              <br></br>
              <Table columns={columns} dataSource={data} />
              <NewParamModal/>
            </Panel>
          </Collapse>
        </>)}
    </>
  );
};

export default SpotCard;
