const axios =  require('axios');
const api = axios.create({
    baseURL: "http://localhost:1800"
});

interface getSpots {
    data: spot[]
}

interface spot {
    coords: {
        coordX: number,
        coordY: number
    },
    _id: string,
    name: string,
    __v: number
}

interface paramBySpotIdResponse {
    name: string,
    spotId: string,
    value: number
}

interface response {
    data: paramBySpotIdResponse[]
}


export function createSpot(spotName: string, coordX: number, coordY: number) {
    axios
      .post('http://localhost:1800/point', {
        "name": spotName,
        "coords": {
            "coordX": coordX,
            "coordY": coordY
        }
    })
  }

  export function createParam(spotId: string, paramName: string, value: number, date: string) {
    axios
    .post(`http://localhost:1800/point/${spotId}`, {
      "name": paramName,
      "value": value,
      "date": date
  })
}
