import { Button, Modal, Form, Input, DatePicker, Select } from 'antd';
import React, { useState } from 'react';
const { Option } = Select;

const Unity  = {
  aluminioDissolvido: 'mg/l',
  arsenioTotal: 'mg/l',
  // "chumboTotal"
  // "cobreDissolvido"
  escherichiaColi: 'NMP/100ml'
  // "cromoTotal"
  // "cadmioTotal"
  // "dbo"
}

const NewParamModal: React.FC = () => {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [paramName, setParamName] = useState('');


  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const [nameSelected, setNameSelected] = useState(Unity);  
  const paramNames = [
    {
    "name": "Aluminio dissolvido",
    "value": "aluminioDissolvido",
    "unity": "mg/l"
   },
   {
    "name":"Arsênio total",
    "value":"arsenioTotal",
    "unity": "mg/l"
   },
   {
    "name":"Chumbo total",
    "value":"chumboTotal",
    "unity": "mg/l"
   },
   {
    "name":"Cobre dissolvido",
    "value":"cobreDissolvido"
   },
   {
    "name":"Escherichia coli",
    "value":"escherichiaColi"
   },
   {
    "name":"Cromo total",
    "value":"cromoTotal"
   },
   {
    "name":"Cádmio total",
    "value":"cadmioTotal"
   },
   {
    "name":"DBO",
    "value":"dbo"
   }
]

const onChange = (value: string) => {
  setParamName(value)
};

const onSearch = (value: string) => {
  console.log('search:', value);
};

console.log(paramName);


  return (
    <>
      <Button type="primary" onClick={showModal}>
        Cadastrar novo parametro
      </Button>
      <Modal title="Novo Paramentro" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
        <Form
        name="basic"
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 16 }}
        initialValues={{ remember: true }}
        onFinish={onFinish}
        autoComplete="off"
      >
        <Form.Item
          label="Nome"
          name="name"
          rules={[{ required: true, message: 'Selecione o nome do parâmetro' }]}
        >
          <Select
            showSearch
            optionFilterProp="children"
            onChange={onChange}
            onSearch={onSearch}
            filterOption={(input, option) =>
              (option!.children as unknown as string).toLowerCase().includes(input.toLowerCase())
            }
          >
            {paramNames.map((param) => 
              <Option  key={param.value} value={param.value}>{param.name}</Option>
            )}
          </Select>
          {/* <Select>
            {paramNames.map((param) => 
              <Select.Option  key={param.value} value={param.value}>{param.name}</Select.Option>
            )}
          </Select> */}
        </Form.Item>

        <Form.Item
          label="Valor"
          name="value"
          rules={[{ required: true, message: 'Insira um valor válido' }]}
        >
          <Input disabled={!nameSelected} placeholder={Unity.aluminioDissolvido}/>
        </Form.Item>
        <Form.Item 
        label="Data de coleta"
        rules={[{ required: true, message: 'Selecione uma data válida' }]}
        >
            <DatePicker/>
          </Form.Item>
      </Form>
      </Modal>
    </>
  );
};

export default NewParamModal;
