import { Collapse, Table, Card } from 'antd';
import React from 'react';
import NewParamModal from './NewParam/NewParamModal';

const { Panel } = Collapse;

const text = `
  A dog is a type of domesticated animal.
  Known for its loyalty and faithfulness,
  it can be found as a welcome guest in many households across the world.
`;

const columns =  [
  {
    title: 'Nome',
    dataIndex: 'name',
    key: 'name',
  },
  {
    title: 'Valor',
    dataIndex: 'value',
    key: 'value',
  },
  {
    title: 'Data da coleta',
    dataIndex: 'date',
    key: 'date',
  },
  {
    title: 'Limite',
    dataIndex: 'limite',
    key: 'limite',
  },
];

const data = [
  {
    key: '1',
    name: 'Cobre dissolvido',
    value: 32,
    date: '23/01/2022',
    limite: 'Viola'
  },
  {
    key: '2',
    name: 'Chumbo total',
    value: 25,
    date: '23/01/2022',
    limite: 'Cumpre'
  },
]

const SpotCard: React.FC = () => {

  return (
    <Collapse  style={{backgroundColor: 'white'}}>
      <Panel header="Nome do ponto" key="1" style={{margin: 20, backgroundColor: '#e8e8e8'}}>
        <div style={{}}>
        <Card title='Coordenada X' style={{width: '50'}}>
          <p>234,65</p>
       </Card>
       <Card title='Coordenada Y'>
          <p>234,65</p>
       </Card>
        </div>
        <Table columns={columns} dataSource={data} />
        <NewParamModal/>
      </Panel>
    </Collapse>
  );
};

export default SpotCard;
