import { Collapse, Table, Card } from 'antd';
import React, { useEffect } from 'react';
import { api, getOi } from '../../Api';
import NewParamModal from '../NewParam/NewParamModal';

const { Panel } = Collapse;

const text = `
  A dog is a type of domesticated animal.
  Known for its loyalty and faithfulness,
  it can be found as a welcome guest in many households across the world.
`;

const columns =  [
  {
    title: 'Nome',
    dataIndex: 'name',
    key: 'name',
  },
  {
    title: 'Valor',
    dataIndex: 'value',
    key: 'value',
  },
  {
    title: 'Data da coleta',
    dataIndex: 'date',
    key: 'date',
  },
  {
    title: 'Limite',
    dataIndex: 'limite',
    key: 'limite',
  },
];

const data = [
  {
    key: '1',
    name: 'Cobre dissolvido',
    value: 32,
    date: '23/01/2022',
    limite: 'Viola'
  },
  {
    key: '2',
    name: 'Chumbo total',
    value: 25,
    date: '23/01/2022',
    limite: 'Cumpre'
  },
]

const SpotCard: React.FC = () => {

  const getOi = async (): Promise<any> => {
    try {
        const { data } = api.get('/point');
        if(data){
            return {
                data
            }
        }        
        return new Error('erro')
    } catch (error) {
        return new Error('erro de cu')
    }
}

