import { Button, Checkbox, Form, Input, DatePicker, Select } from 'antd';
import React, { Dispatch, SetStateAction, useState } from 'react';

type NewParamFormProp  = {
  created: boolean
};

const NewParamForm =  ({created}: NewParamFormProp): JSX.Element  => {
  const [nameSelected, setNameSelected] = useState(false);  
  const onFinish = (values: any) => {
    console.log('Success:', values);
  };

  const paramNames = [
    {
    "name": "Aluminio dissolvido",
    "value": "aluminioDissolvido"
   },
   {
    "name":"Arsênio total",
    "value":"arsenioTotal"
   },
   {
    "name":"Chumbo total",
    "value":"chumboTotal"
   },
   {
    "name":"Cobre dissolvido",
    "value":"cobreDissolvido"
   },
   {
    "name":"Escherichia coli",
    "value":"escherichiaColi"
   },
   {
    "name":"Cromo total",
    "value":"cromoTotal"
   },
   {
    "name":"Cádmio total",
    "value":"cadmioTotal"
   },
   {
    "name":"DBO",
    "value":"dbo"
   }
]

  enum Unity {
      "aluminioDissolvido" = 'mg/l'
      // "arsenioTotal"
      // "chumboTotal"
      // "cobreDissolvido"
      // "escherichiaColi"
      // "cromoTotal"
      // "cadmioTotal"
      // "dbo"
  }

  const onFinishFailed = (errorInfo: any) => {
    console.log('Failed:', errorInfo);
  };

  console.log(nameSelected);

  return (
    <Form
      name="basic"
      labelCol={{ span: 8 }}
      wrapperCol={{ span: 16 }}
      initialValues={{ remember: true }}
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
      autoComplete="off"
    >
      <Form.Item
        label="Nome"
        name="name"
        rules={[{ required: true, message: 'Selecione o nome do parâmetro' }]}
      >
        <Select>
          {paramNames.map((param) => <Select.Option onClick={() => setNameSelected(true)} key={param.value} value={param.value}>{param.name}</Select.Option>)}
        </Select>
      </Form.Item>

      <Form.Item
        label="Valor"
        name="value"
        rules={[{ required: true, message: 'Insira um valor válido' }]}
      >
        <Input disabled={nameSelected} placeholder={Unity.aluminioDissolvido}/>
      </Form.Item>
      <Form.Item 
      label="Data de coleta"
      rules={[{ required: true, message: 'Selecione uma data válida' }]}
      >
          <DatePicker />
        </Form.Item>
    </Form>
  );
};

export default NewParamForm;
