import { Collapse, Table, Card } from 'antd';
import React, { useEffect, useState } from 'react';
import NewParamModal from '../NewParam/NewParamModal';
const axios = require('axios');

const { Panel } = Collapse;

const columns =  [
  {
    title: 'Nome',
    dataIndex: 'name',
    key: 'name',
  },
  {
    title: 'Valor',
    dataIndex: 'value',
    key: 'value',
  },
  {
    title: 'Data da coleta',
    dataIndex: 'date',
    key: 'date',
  },
  {
    title: 'Limite',
    dataIndex: 'limite',
    key: 'limite',
  },
];

const data = [
  {
    key: '1',
    name: 'Cobre dissolvido',
    value: 32,
    date: '23/01/2022',
    limite: 'Viola'
  },
  {
    key: '2',
    name: 'Chumbo total',
    value: 25,
    date: '23/01/2022',
    limite: 'Cumpre'
  },
]

const SpotCard: React.FC = () => {
  const [spots, setSpots] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:1800/point").then((res: any) => {
      setSpots(res.data)
    })
  }, [])

  return (
    <>
      {}
    </>
  
    // <Collapse  style={{backgroundColor: 'white'}}>
