const axios =  require('axios');
const api = axios.create({
    baseURL: "http://localhost:1800"
});

interface getSpots {
    data: spot[]
}

interface spot {
    coords: {
        coordX: number,
        coordY: number
    },
    _id: string,
    name: string,
    __v: number
}

const getOi = async (): Promise<getSpots | Error> => {
    try {
        const { data, status } = api.get('/point');
        if(data){
            return {
                data
            }
        }

        return new Error('n sei', status)
        
    } catch (error) {
        return new Error('erro de cu')
    }
}

export const Service = {
    getOi,
}

export function fetchSpots(){
    axios.get("http://localhost:1800/point")
}


export function createSpot(spotName: string, coordX: number, coordY: number) {
    axios
      .post('http://localhost:1800/point', {
        "name": spotName,
        "coords": {
            "coordX": coordX,
            "coordY": coordY
        }
    })
  }
