import { Button, Modal, Form, Input, DatePicker, Select } from 'antd';
import React, { useState } from 'react';
const axios = require('axios');

const NewSpotModal: React.FC = () => {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [spotName, setName] = useState('');
  const [spotCoordX, setCoordX] = useState(0);
  const [spotCoordY, setCoordY] = useState(0);


  function createPost(spotName: string, coordX: number, coordY: number) {
    axios
      .post('localhost:1800/point', {
        "name": spotName,
        "coords": {
            "coordX": coordX,
            "coordY": coordY
        }
    })
  }

  console.log(spotName)

  const onFinish = (values: any) => {
    console.log('Success:', values);
  };

  const onFinishFailed = (errorInfo: any) => {
    console.log('Failed:', errorInfo);
  };

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
    createPost(spotName, spotCoordX);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <>
      <Button type="text" onClick={showModal} style={{borderColor: '#e8e8e8', width: '50%' }}>
        Novo Ponto
      </Button>
      <Modal title="Basic Modal" visible={isModalVisible} onOk={() => handleOk(spotName, spotCoordX, spotCoordY )} onCancel={handleCancel}>
        <Form
          name="basic"
          labelCol={{ span: 8 }}
          wrapperCol={{ span: 16 }}
          initialValues={{ remember: true }}
          onFinish={onFinish}
          onFinishFailed={onFinishFailed}
          autoComplete="off"
        >
          <Form.Item
            label="Nome"
            name="name"
            rules={[{ required: true, message: 'Please input your username!' }]}
          >
              <Input value={spotName} onBlur={(event) => setName(event.target.value)}/>
          </Form.Item>

          <Form.Item label="Coordenadas"         
            rules={[{ required: true, message: 'Please input your username!' }]}
          >
            <Input.Group compact>
              <Form.Item
                name="coordX"
                noStyle
                rules={[{ required: true, message: 'Province is required' }]}
              >
                <Input name="coordX" style={{ width: '45%', marginRight: '5%' }} placeholder="Coordenada X" />
              </Form.Item>
              <Form.Item
                name={['address', 'street']}
                noStyle
                rules={[{ required: true, message: 'Street is required' }]}
              >
                <Input name="coordY" style={{ width: '45%' }} placeholder="Coordenada Y" />
              </Form.Item>
            </Input.Group>
          </Form.Item>
        </Form>
      </Modal>
    </>
  );
};

export default NewSpotModal;
